import React, {useEffect, useMemo, useState} from 'react';
import MaterialReactTable from 'material-react-table';
import axios from 'axios';


const records = [
    {
        index: '1',
        order_number: 7777,
        prices_in_dollars: 940,
        prices_in_rubles: 778000,
        delivery_date: "24.03.2023"
  },
    {
        index: '2',
        order_number: 7777,
        prices_in_dollars: 1000,
        prices_in_rubles: 778000,
        delivery_date: '24.03.2023'
  }
];

const Table = () => {


    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const result = await axios(
                'https://jsonplaceholder.typicode.com/posts',
            );
            setPosts(result.data);
        };
        fetchData();
    }, []);
  const fetchData = async () => {
    const response = await axios.get(
      "0.0.0.0:9090/table"
    );

    setRecords(response.data);
  };

  fetchData();

  const columns = useMemo(
    () => [
      {
        accessorKey: 'index',
        header: '№',
           size: 10
      },
      {
        accessorKey: 'order_number',
        header: '№ заказа',
           size: 100
      },
      {
        accessorKey: 'prices_in_dollars',
        header: 'Стоимость в USD',
          size: 50
      },
      {
        accessorKey: 'prices_in_rubles',
        header: 'Стоимость в RUB',
           size: 80,
      },
      {
        accessorKey: 'delivery_date',
        header: 'Дата поставки',
           size: 50
      },
    ],
    [],
  );

  return <MaterialReactTable columns={columns} data={records} classname={"Table"}
                             muiTableHeadCellProps={{
    sx: {
      fontSize: {
        xl: '13px',
        lg: '10px',
      },
    },
  }}/>;
};

export default Table;
